CREATE TABLE animales (
    ID_animal VARCHAR(255) PRIMARY KEY,
    Sexo INT,
    OD_1 FLOAT,
    OI_1 FLOAT,
    OD_2 FLOAT,
    OI_2 FLOAT,
    OD_3 FLOAT,
    OI_3 FLOAT,
    OD_4 FLOAT,
    OI_4 FLOAT,
    Edad_Opto FLOAT,
    Dato_OPT FLOAT,
    OD_5 FLOAT,
    OI_5 FLOAT,
    degenera INT,
    Edad_ERG FLOAT
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

